function y = hht(x, t, thr)
    % make row vectors
    x = x(:).';
    t = t(:).';
    % Parameters
    MAX_SIFT = 20;   % maximum number of sifting iterations K to avoid infinite loop

    imfs = [];       % each row will be one IMF
    residual = x;

    while true
        %---- Step 8: check if residual has <= 3 non-boundary extrema ----
        [~, maxVal, ~, minVal] = find_extrema(residual);
        if numel(maxVal)+numel(minVal) <= 3
            break;
        end

        % Start to extract the next IMF from current residual
        h = residual;           % this is hk(t) at iteration k
        k = 0;

        while true
            k = k + 1;

            %---- Step 2 & 4: find local maxima and minima of h(t) ----
            [maxIdx, maxVal, minIdx, minVal] = find_extrema(h);

            %---- Step 3 & 5: connect peaks/dips with cubic spline envelopes ----
            u1   = spline(t(maxIdx), maxVal, t);   % cubic B-spline envelope
            u0   = spline(t(minIdx), minVal, t);

            %---- Step 6: mean envelope z(t) and new residue h_k(t) ----
            z    = 0.5 * (u1 + u0);
            hNew = h - z;

            %---- Step 7: check if hNew(t) is an IMF ----
            [~, maxValH, ~, minValH] = find_extrema(hNew);

            % (1) all local maxima > 0 and all local minima < 0
            cond1 = all(maxValH > 0) && all(minValH < 0);

            % (2) |(u1(t)+u0(t))/2| < threshold for all t
            cond2 = max(abs(z)) < thr;

            if (cond1 && cond2) || (k >= MAX_SIFT)
                % accept as IMF
                h = hNew;
                break;
            else
                % continue sifting
                h = hNew;
            end
        end

        % Step 8: store IMF and update residual: x0(t) = x(t) - sum c_s(t)
        imfs = [imfs; h];%#ok<AGROW>
        residual = residual - h;
    end

    y = imfs;   % each row is one IMF

end

function [maxIdx, maxVal, minIdx, minVal] = find_extrema(sig)
% Find local maxima and minima (non-boundary).
    N = numel(sig);
    maxIdx = [];
    minIdx = [];
    for i = 2:(N-1)
        if sig(i) >= sig(i-1) && sig(i) > sig(i+1)
            maxIdx(end+1) = i; %#ok<AGROW>
        elseif sig(i) <= sig(i-1) && sig(i) < sig(i+1)
            minIdx(end+1) = i; %#ok<AGROW>
        end
    end
    maxVal = sig(maxIdx);
    minVal = sig(minIdx);
end
% run the example
t   = 0:0.01:10;
x   = 0.2*t + cos(2*pi*t) + 0.4*cos(10*pi*t);
thr = 0.2;
y = hht(x, t, thr);
imf_sum = sum(y, 1);
x0 = x - imf_sum;
figure; %[output:4ab77a41]
subplot(4,1,1); plot(t,x);      title('x(t)'); %[output:4ab77a41]
subplot(4,1,2); plot(t,y(1,:)); title('IMF 1'); %[output:4ab77a41]
subplot(4,1,3); plot(t,y(2,:)); title('IMF 2'); %[output:4ab77a41]
subplot(4,1,4); plot(t,x0); title('Trend'); %[output:4ab77a41]

